        const { id_paciente } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM pacientes WHERE id_paciente = ?", id_paciente);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

